SELECT ?grand_parent, COUNT(?grand_children) AS ?number_of_grand_children
FROM ?family
WHERE {
  /u<joe> AS ?grand_parent "parent_of"@[] ?offspring .
  ?offspring               "parent_of"@[] ?grand_children
}
GROUP BY ?grand_parent;

SELECT ?gender
FROM ?family
WHERE {
  ?mammal "is_a"@[]   /t<mammal> .
  ?mammal "gender"@[] ?gender_node ID ?gender
}
GROUP BY ?gender;

SELECT ?gender, COUNT(?gender_node) AS ?count
FROM ?family
WHERE {
  ?mammal "is_a"@[]   /t<mammal> .
  ?mammal "gender"@[] ?gender_node ID ?gender
}
GROUP BY ?gender;

SELECT ?grand_parent, ?grand_children
FROM ?family
WHERE {
  /u<joe> AS ?grand_parent "parent_of"@[] ?offspring .
  ?offspring               "parent_of"@[] ?grand_children .
  ?grand_children          "gender"@[]    /g<female>
}
GROUP BY ?grand_parent, ?grand_children;

SELECT ?grand_parent, ?grand_children
FROM ?family
WHERE {
  /u<joe> AS ?grand_parent "parent_of"@[] ?offspring .
  ?offspring               "parent_of"@[] ?grand_children .
  ?grand_children          "gender"@[]    /g<male>
}
GROUP BY ?grand_parent, ?grand_children;

SELECT ?gender, COUNT(DISTINCT ?grand_children) AS ?count
FROM ?family
WHERE {
  /u<joe> AS ?grand_parent "parent_of"@[] ?offspring .
  ?offspring               "parent_of"@[] ?grand_children .
  ?grand_children          "is_a"@[]      /t<mammal> .
  ?grand_children          "gender"@[]    ?gender_node ID ?gender
}
GROUP BY ?gender;
